  `menu_icon` varchar(255) DEFAULT NULL COMMENT '栏目在前端显示的icon图标'
) ENGINE=MyISAM AUTO_INCREMENT=92 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='权限表';

